			function getAge($date) { // Y-m-d format
				$now = explode("-", date('Y-m-d'));
				$dob = explode("-", $date);
				$dif = $now[0] - $dob[0];
				if ($dob[1] > $now[1]) { // birthday month has not hit this year
					$dif -= 1;
				}
				elseif ($dob[1] == $now[1]) { // birthday month is this month, check day
					if ($dob[2] > $now[2]) {
						$dif -= 1;
					}
					elseif ($dob[2] == $now[2]) { // Happy Birthday!
						$dif = $dif;
					};
				};
				return $dif;
			}
